membro( X, [X|_] ).
membro( X, [_|R] ) :- membro( X, R ).

verfilme(X,W,Z):- findall(Y,(filme(Y,H,_,U,_,_,_,W), H>=Z, membro(X,U)),K), print(K).

verfilmeator(X):- findall(Y,(filme(Y,_,_,_,_,_,U,_), membro(X,U)),K), print(K).

